UPDATE `#__components` SET enabled='0' WHERE `option` IN ('com_banners','com_weblinks','com_contacts','com_search','com_wrapper','com_mailto','com_massmail');
UPDATE `#__components` SET params='show_headings=1\nshow_name=1\nshow_articles=1\nshow_link=1\nshow_cat_description=1\nshow_cat_items=1\nshow_feed_image=1\nshow_feed_description=1\nshow_item_description=1\nfeed_word_count=0\n\n' WHERE name='News Feeds';
UPDATE `#__components` SET params='upload_extensions=bmp,csv,doc,epg,gif,ico,jpg,odg,odp,ods,odt,pdf,png,ppt,swf,txt,xcf,xls,BMP,CSV,DOC,EPG,GIF,ICO,JPG,ODG,ODP,ODS,ODT,PDF,PNG,PPT,SWF,TXT,XCF,XLS\nupload_maxsize=10000000\nfile_path=site/media\nimage_path=site/media/images\nrestrict_uploads=1\nallowed_media_usergroup=3\ncheck_mime=1\nimage_extensions=bmp, gif, jpg, png\nignore_extensions=\nupload_mime=image/jpeg, image/gif, image/png, image/bmp, application/x-shockwave-flash, application/msword, application/excel, application/pdf, application/powerpoint, text/plain, application/x-zip\nupload_mime_illegal=text/html\nenable_flash=0\n\n' WHERE name='Media Manager';
UPDATE `#__components` SET params='show_noauth=0\nshow_title=1\nlink_titles=0\nshow_intro=1\nshow_section=0\nlink_section=0\nshow_category=0\nlink_category=0\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_item_navigation=0\nshow_readmore=1\nshow_vote=0\nshow_icons=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nshow_hits=0\nfeed_summary=0\nfilter_tags=\nfilter_attritbutes=\n\n' WHERE name='Articles';
UPDATE `#__components` SET params='allowUserRegistration=1\nnew_usertype=Registered\nuseractivation=1\nfrontend_userparams=1\nallow_duplicate_emails=0\n\n' WHERE name='User Manager';
UPDATE `#__modules` SET position='user3' WHERE title='Main Menu';
UPDATE `#__modules` SET showtitle='0' WHERE title='Main Menu';
UPDATE `#__modules` SET published='0' WHERE title='Quick Icons';
UPDATE `#__modules` SET module='mod_hubmenu' WHERE title='Admin Menu';
UPDATE `#__modules` SET params='menutype=mainmenu\nmenu_style=list\nstartLevel=0\nendLevel=0\nshowAllChildren=1\nwindow_open=\nshow_whitespace=0\ncache=1\ntag_id=\nclass_sfx=\nmoduleclass_sfx=_menu\nmaxdepth=10\nmenu_images=0\nmenu_images_align=0\nmenu_images_link=0\nexpand_menu=0\nactivate_parent=0\nfull_active_id=0\nindent_image=0\nindent_image1=\nindent_image2=\nindent_image3=\nindent_image4=\nindent_image5=\nindent_image6=\nspacer=\nend_spacer=\n\n' WHERE title='Main Menu';

